// @ts-check
const { defineConfig, devices } = require('@playwright/test')

module.exports = defineConfig({
  testDir: './e2e/features',
  outputDir: './test-results',
  /* Maximum time one test can run for. */
  timeout: 30 * 1000,
  expect: {
    /* Maximum time expect() should wait for the condition to be met. */
    timeout: 5000
  },
  /* Run tests in files in parallel */
  fullyParallel: true,
  /* Fail the build on CI if you accidentally left test.only in the source code. */
  forbidOnly: !!process.env.CI,
  /* Retry on CI only */
  retries: process.env.CI ? 2 : 0,
  /* Opt out of parallel tests on CI. */
  workers: process.env.CI ? 1 : 3,
  /* Reporter to use. See https://playwright.dev/docs/test-reporters */
  reporter: 'html',
  use: {
    launchOptions: {
      slowMo: 200
    },
    /* Maximum time each action such as `click()` can take. Defaults to 0 (no limit). */
    actionTimeout: 0,
    /* Collect trace when retrying the failed test. See https://playwright.dev/docs/trace-viewer */
    trace: 'on-first-retry'
  },

  /* Configure projects for major browsers */
  projects: [
    {
      name: 'chromium',
      use: { ...devices['Desktop Chrome'] }
    },

    {
      name: 'firefox',
      use: { ...devices['Desktop Firefox'] }
    }
  ]

  /* Folder for test artifacts such as screenshots, videos, traces, etc. */
  // outputDir: 'test-results/',
})

// @ts-check
const { test, expect } = require('playwright-test-coverage')
const createSelectors = require('../../test-utils/global-variables/selectors')
const {
  animationTooManyFramesGifCustomInterval,
  activeAnimationWidget,
  animationTooManyFramesGif,
  animationProjectionRotated
} = require('../../test-utils/global-variables/querystrings')

let page
let selectors

test.describe.configure({ mode: 'serial' })

test.beforeAll(async ({ browser }) => {
  page = await browser.newPage()
  selectors = createSelectors(page)
})

test.afterAll(async () => {
  await page.close()
})

test('Clicking the animation widget button opens the widget', async () => {
  const { createGifIcon, arcticRotationResetButton, modalCloseButton, rotationDialogOkButton } = selectors
  await page.goto(animationProjectionRotated)
  await modalCloseButton.click()
  await createGifIcon.click()
  await expect(arcticRotationResetButton).toHaveText('-18')
  await rotationDialogOkButton.click()
  await expect(arcticRotationResetButton).toHaveText('0')
})

test('GIF selection preview is Accurate and selections that are too high disable GIF download', async () => {
  const {
    createGifIcon,
    gifPreviewStartDate,
    gifPreviewEndDate,
    gifPreviewFrameRateValue,
    gifPreviewEndResolutionSelector,
    gifDownloadButton,
    modalCloseButton
  } = selectors
  await page.goto(activeAnimationWidget)
  await modalCloseButton.click()
  await createGifIcon.click()
  await expect(gifPreviewStartDate).toHaveText('2018 MAR 28')
  await expect(gifPreviewEndDate).toHaveText('2018 APR 04')
  await expect(gifPreviewFrameRateValue).toHaveText('3 Frames Per Second')
  const gifMaxSize = page.locator('.gif-max-size')
  await expect(gifMaxSize).toHaveText('8200px')
  const gifBox = page.locator('#wv-checkbox-gif')
  await expect(gifBox).toBeChecked()
  const gifResolution = page.locator('#gif-resolution')
  await gifResolution.click()
  await page.locator('#gif-resolution').selectOption('2')
  await expect(gifPreviewEndResolutionSelector).toHaveValue('2')
  await expect(gifDownloadButton).toBeDisabled()
})

test('GIF download is disabled when too many frames would be requested with standard interval', async () => {
  const { modalCloseButton } = selectors
  await page.goto(animationTooManyFramesGif)
  await modalCloseButton.click()
  const createGif = page.locator('#create-gif-button')
  await expect(createGif).toHaveClass(/disabled/)
})

test('GIF download is disabled when too many frames would be requested with custom interval', async () => {
  const { modalCloseButton } = selectors
  await page.goto(animationTooManyFramesGifCustomInterval)
  await modalCloseButton.click()
  const createGif = page.locator('#create-gif-button')
  await expect(createGif).toHaveClass(/disabled/)
})

const { test, expect } = require('playwright-test-coverage')
const createSelectors = require('../../test-utils/global-variables/selectors')
const { swipeAndAIsActive, spyAndBIsActive, opacityAndBIsActive } = require('../../test-utils/global-variables/querystrings')

let page
let selectors

test.describe.configure({ mode: 'serial' })

test.beforeAll(async ({ browser }) => {
  const context = await browser.newContext({
    viewport: { width: 375, height: 667 }
  })
  page = await context.newPage()
  selectors = createSelectors(page)
})

test.afterAll(async () => {
  await page.close()
})

test('Mobile comparison A|B toggle buttons are visible and only A is selected by default', async () => {
  const { modalCloseButton } = selectors
  await page.goto(swipeAndAIsActive)
  await modalCloseButton.click()
  const aMobileCompareButton = page.locator('.comparison-mobile-select-toggle > div:nth-child(1)')
  const bMobileCompareButton = page.locator('.comparison-mobile-select-toggle > div:nth-child(2)')
  await expect(aMobileCompareButton).toHaveClass(/compare-btn-selected/)
  await expect(bMobileCompareButton).not.toHaveClass(/compare-btn-selected/)
})

// toggle select B change compare mode date to B
test('Toggling to B compare side changes mobile date picker date', async () => {
  const { mobileDatePickerSelectButton } = selectors
  const bMobileCompareButton = page.locator('.comparison-mobile-select-toggle > div:nth-child(2)')
  // confirm initial A mobile date picker date
  await expect(mobileDatePickerSelectButton).toHaveText('2018 AUG 17')
  // click B compare toggle button and confirm B mobile date picker date
  await bMobileCompareButton.click()
  await expect(mobileDatePickerSelectButton).toHaveText('2018 AUG 16')
})

test('Expand mobile layer list and confirm comparison mode button is present and toggles compare mode', async () => {
  const {
    collapsedLayerButton,
    compareButton,
    aTab,
    bTab,
    compareButtonText
  } = selectors
  await collapsedLayerButton.click()
  await expect(compareButton).toBeVisible()
  await expect(aTab).toBeVisible()
  await expect(bTab).toBeVisible()
  await expect(compareButtonText).toHaveText('Exit Comparison Mode')
  await compareButton.click()
  await expect(aTab).not.toBeVisible()
  await expect(bTab).not.toBeVisible()
  await expect(compareButton).toBeVisible()
  await expect(compareButtonText).toHaveText('Start Comparison Mode')
})

// B compare button toggle is selected on B permalink load and A is not selected
test('B compare button toggle is only selected on B permalink load', async () => {
  await page.goto(spyAndBIsActive)
  const aMobileCompareButton = page.locator('.comparison-mobile-select-toggle > div:nth-child(1)')
  const bMobileCompareButton = page.locator('.comparison-mobile-select-toggle > div:nth-child(2)')
  await expect(aMobileCompareButton).not.toHaveClass(/compare-btn-selected/)
  await expect(bMobileCompareButton).toHaveClass(/compare-btn-selected/)
})

// load comparison SPY mode and verify that it reverts to SWIPE mode
test('Mobile SPY mode reverts to SWIPE mode', async () => {
  const { swipeDragger } = selectors
  await page.goto(spyAndBIsActive)
  await expect(swipeDragger).toBeVisible()
})

// load comparison OPACITY mode and verify that it reverts to SWIPE mode
test('Mobile OPACITY mode reverts to SWIPE mode', async () => {
  const { swipeDragger } = selectors
  await page.goto(opacityAndBIsActive)
  await expect(swipeDragger).toBeVisible()
})

// @ts-check
const { test, expect } = require('playwright-test-coverage')
const createSelectors = require('../../test-utils/global-variables/selectors')
const { swipeAndAIsActive, spyAndBIsActive } = require('../../test-utils/global-variables/querystrings')

let page
let selectors

test.describe.configure({ mode: 'serial' })

test.beforeAll(async ({ browser }) => {
  page = await browser.newPage()
  selectors = createSelectors(page)
})

test.afterAll(async () => {
  await page.close()
})

test('Animation is disabled when compare mode active', async () => {
  const { animationButtonCase, animationWidget, modalCloseButton } = selectors
  await page.goto(swipeAndAIsActive)
  await modalCloseButton.click()
  await expect(animationButtonCase).toHaveClass(/wv-disabled-button/)
  const disableMessage = 'Animation feature is deactivated when Compare feature is active'
  await expect(animationButtonCase).toHaveAttribute('aria-label', disableMessage)
  await animationButtonCase.hover()
  const tooltip = page.locator('.tooltip-inner')
  await expect(tooltip).toHaveText(disableMessage)
  await animationButtonCase.click()
  await expect(animationWidget).not.toBeVisible()
})

test('Image download is disabled when compare mode active', async () => {
  const { snapshotToolbarButton } = selectors
  const disableMessage = 'You must exit comparison mode to use the snapshot feature'
  await expect(snapshotToolbarButton).toHaveClass(/disabled/)
  await expect(snapshotToolbarButton).toHaveAttribute('aria-label', disableMessage)
  await page.waitForTimeout(2000)
  await page.locator('#snapshot-btn-wrapper').click()
  const imageRes = page.locator('#wv-image-resolution')
  await expect(imageRes).not.toBeVisible()
})

test('Data download is disabled when compare mode active', async () => {
  const { dataDownloadTabButton } = selectors
  const disableMessage = 'You must exit comparison mode to download data'
  await expect(dataDownloadTabButton).toHaveClass(/disabled/)
  await expect(dataDownloadTabButton).toHaveAttribute('aria-label', disableMessage)
  const downloadTab = page.locator('#download-sidebar-tab')
  await downloadTab.hover()
  const tooltipSelector = page.locator('.tooltip-inner')
  await expect(tooltipSelector).toContainText(disableMessage)
  // Clicking does not switch tabs
  await dataDownloadTabButton.click()
  const smartHandoffPanel = page.locator('#smart-handoff-side-panel')
  await expect(smartHandoffPanel).not.toBeVisible()
})

test('Events disabled when compare mode active', async () => {
  const { eventsSidebarTabButton } = selectors
  const disableMessage = 'You must exit comparison mode to use the natural events feature'
  await eventsSidebarTabButton.click()
  const eventsContent = page.locator('#wv-eventscontent')
  await expect(eventsContent).not.toBeVisible()
  await expect(eventsSidebarTabButton).toHaveClass(/disabled/)
  await expect(eventsSidebarTabButton).toHaveAttribute('aria-label', disableMessage)
  await page.locator('#events-sidebar-tab').hover()
  const tooltipSelector = page.locator('.tooltip-inner')
  await expect(tooltipSelector).toContainText(disableMessage)
})

test('Removing layer removes correct layer from correct layer group', async () => {
  const { ModisTrueColorLayerA, ModisTrueColorLayerB, bTab } = selectors
  await expect(ModisTrueColorLayerA).toBeVisible()
  await ModisTrueColorLayerA.hover()
  const removeLayerBtn = page.locator('#close-activeMODIS_Terra_CorrectedReflectance_TrueColor')
  await removeLayerBtn.click()
  await expect(ModisTrueColorLayerA).not.toBeVisible()
  await expect(ModisTrueColorLayerB).not.toBeVisible()
  await bTab.click()
  await expect(ModisTrueColorLayerB).toBeVisible()
})

test('Collapse layer list with B state and test label shows correct number of layers', async () => {
  const { toggleButton, collapsedToggleButton, modalCloseButton } = selectors
  await page.goto(spyAndBIsActive)
  await modalCloseButton.click()
  await expect(collapsedToggleButton).not.toBeVisible()
  await toggleButton.click()
  await expect(collapsedToggleButton).toBeVisible()
  const layerCount = page.locator('#accordion-toggler-button .layer-count')
  await expect(layerCount).toHaveText('6 Layers')
  await collapsedToggleButton.click()
  const referenceFeatures = page.locator('#activeB-Reference_Features_15m')
  await expect(referenceFeatures).toBeVisible()
})

test('If you exit A|B with B selection active, the active state will then be the B state', async () => {
  const { compareButton } = selectors
  const correctedReflectanceSNPP = page.locator('#activeB-VIIRS_SNPP_CorrectedReflectance_TrueColor')
  const closeReflectanceSNPP = page.locator('#close-activeBVIIRS_SNPP_CorrectedReflectance_TrueColor')
  const correctedReflectanceAqua = page.locator('#activeB-MODIS_Aqua_CorrectedReflectance_TrueColor')
  const closeReflectanceAqua = page.locator('#close-activeBMODIS_Aqua_CorrectedReflectance_TrueColor')
  const referenceLabels = page.locator('#activeB-Reference_Labels_15m')
  const closeRefLabels = page.locator('#close-activeBReference_Labels_15m')
  const referenceFeatures = page.locator('#activeB-Reference_Features_15m')
  const closeRefFeatures = page.locator('#close-activeBReference_Features_15m')
  const coastlines = page.locator('#activeB-Coastlines_15m')
  const correctedReflectanceTerra = page.locator('#activeB-MODIS_Terra_CorrectedReflectance_TrueColor')
  await expect(correctedReflectanceSNPP).toBeVisible()
  await expect(correctedReflectanceAqua).toBeVisible()
  await referenceLabels.hover()
  await closeRefLabels.click()
  await referenceFeatures.hover()
  await closeRefFeatures.click()
  await correctedReflectanceSNPP.hover()
  await closeReflectanceSNPP.click()
  await correctedReflectanceAqua.hover()
  await closeReflectanceAqua.click()
  await compareButton.click()
  await expect(coastlines).toBeVisible()
  await expect(correctedReflectanceTerra).toBeVisible()
  await expect(correctedReflectanceSNPP).not.toBeVisible()
  await expect(correctedReflectanceAqua).not.toBeVisible()
})

// @ts-check
const { test, expect } = require('playwright-test-coverage')
const { skipTour } = require('../../test-utils/global-variables/querystrings')

let page

test.describe.configure({ mode: 'serial' })

test.beforeAll(async ({ browser }) => {
  page = await browser.newPage()
})

test.afterAll(async () => {
  await page.close()
})

test('Ensure that dev test mode is not on', async () => {
  await page.goto(skipTour)
  const devBlock = page.locator('#dev-block')
  await expect(devBlock).not.toBeVisible()
})

const { expect } = require('@playwright/test')

/**
 * Switches the projection
 * @param {Object} page - Playwright object representing the browser page.
 * @param {string} proj - The specified projection ex: 'geographic', 'arctic' etc..
 */
const switchProjections = async (page, proj) => {
  await page.locator('#wv-proj-button').click()
  await page.locator(`#change-${proj}-button`).click()
}

const openImageDownloadPanel = async (page) => {
  await page.locator('#wv-image-button').click()
}

const clickDownload = async (page) => {
  await page.locator('.wv-image-button').click()
}

const closeImageDownloadPanel = async (page) => {
  await page.locator('.modal-close-btn').click()
}

const zoomIn = async (page) => {
  await page.locator('button.wv-map-zoom-in').click()
}

const zoomOut = async (page) => {
  await page.locator('button.wv-map-zoom-out').click()
}

/**
 * Drag one of the draggers on the timeline.
 * To find the x parameter you need to inspect the exact date on the timeline you want to drag to
 * and find the x attribute value in the rect element.
 * @param {Object} page - Playwright object representing the browser page.
 * @param {string} draggerEl - 'a' or 'b'. Represents draggerA or draggerB
 * @param {string} x - Represents exact point on timeline to drag to.
 */
const timelineDrag = async (page, draggerEl, x) => {
  const draggerA = await page.locator('.timeline-dragger.draggerA')
  const draggerB = await page.locator('.timeline-dragger.draggerB')
  const dragger = draggerEl === 'a' ? draggerA : draggerB
  const dragDate = `rect.axis-grid-rect[width="12"][height="65"][x="${x}"][fill="transparent"]`
  const dragLocation = await page.locator(dragDate)
  await dragger.dragTo(dragLocation)
}

/**
 * Returns the month date of date selector ex: 'AUG17'
 * @param {Object} page - Playwright object representing the browser page.
 */
const dateSelectorMonthDay = async (page) => {
  const dateSelectorDayInput = await page.locator('#date-selector-main .input-wrapper-day input')
  const dateSelectorMonthInput = await page.locator('#date-selector-main .input-wrapper-month input')
  const day = dateSelectorDayInput.innerText()
  const month = dateSelectorMonthInput.innerText()
  return month + day
}

const assertDefaultLayers = async (page) => {
  const layerItem = page.locator('.item.productsitem')
  const refLabels = page.locator('#active-Reference_Labels_15m')
  const refFeatures = page.locator('#active-Reference_Features_15m')
  const coastlines = page.locator('#active-Coastlines_15m')
  const trueColorSNPP = page.locator('#active-VIIRS_SNPP_CorrectedReflectance_TrueColor')
  const trueColorAqua = page.locator('#active-MODIS_Aqua_CorrectedReflectance_TrueColor')
  const trueColorMODIS = page.locator('#active-MODIS_Terra_CorrectedReflectance_TrueColor')
  const trueColorNOAA = page.locator('#active-VIIRS_NOAA20_CorrectedReflectance_TrueColor')
  await expect(layerItem).toHaveCount(7)
  await expect(refLabels).toBeVisible()
  await expect(refFeatures).toBeVisible()
  await expect(coastlines).toBeVisible()
  await expect(trueColorSNPP).toBeVisible()
  await expect(trueColorAqua).toBeVisible()
  await expect(trueColorMODIS).toBeVisible()
  await expect(trueColorNOAA).toBeVisible()
}

const assertCategories = async (page) => {
  const categoriesContainer = page.locator('.category-masonry-case')
  const legacy = page.locator('#legacy-all')
  const airQuality = page.locator('#air-quality')
  const ashPlumes = page.locator('#ash-plumes')
  const drought = page.locator('#drought')
  const fires = page.locator('#fires')
  const floods = page.locator('#floods')
  const shipping = page.locator('#shipping')
  const dust = page.locator('#dust-storms')
  const storms = page.locator('#severe-storms')
  const smoke = page.locator('#smoke-plumes')
  const vegetation = page.locator('#vegetation')
  const other = page.locator('#legacy-other')
  await expect(categoriesContainer).toBeVisible()
  await expect(legacy).toBeVisible()
  await expect(airQuality).toBeVisible()
  await expect(ashPlumes).toBeVisible()
  await expect(drought).toBeVisible()
  await expect(fires).toBeVisible()
  await expect(floods).toBeVisible()
  await expect(shipping).toBeVisible()
  await expect(dust).toBeVisible()
  await expect(storms).toBeVisible()
  await expect(smoke).toBeVisible()
  await expect(vegetation).toBeVisible()
  await expect(other).toBeVisible()
}

/**
 * Check the layer order in the sidebar and compare it agaisnt an array of ordered layers
 * @param {Object} page - Playwright object representing the browser page.
 * @param {string} layerContainer - A string for identifying each layer <li> element in the sidebar
 * @param {Array} orderedLayers - An array of strings representing the expected layer ordering
 */
const assertLayerOrdering = async (page, layerContainer, orderedLayers) => {
  const layers = await page.$$(layerContainer)
  const layerIDs = await Promise.all(layers.map(async (layer) => {
    const layerID = await layer.getAttribute('id')
    return layerID
  }))
  expect(layerIDs).toEqual(orderedLayers)
}

/**
 * Create a distance measurement
 * @param {Object} page - Playwright object representing the browser page.
 * @param {Array} start - An array of two integers representing the starting x & y position
 * @param {Array} finish - An array of two integers representing the starting x & y position
 */
const createDistanceMeasurement = async (page, start, finish) => {
  await page.locator('#wv-measure-button').click()
  await page.locator('#measure-distance-button').click()
  await page.mouse.click(start[0], start[1])
  await page.mouse.dblclick(finish[0], finish[1])
}

const createAreaMeasurement = async (page, pointOne, pointTwo, pointThree) => {
  await page.locator('#wv-measure-button').click()
  await page.locator('#measure-area-button').click()
  await page.mouse.click(pointOne[0], pointOne[1])
  await page.mouse.click(pointTwo[0], pointTwo[1])
  await page.mouse.dblclick(pointThree[0], pointThree[1])
}

const localStorageEnabled = () => {
  let enabled
  try {
    if (window.localStorage) {
      const uid = new Date().toString()
      window.localStorage.setItem(uid, uid)
      enabled = window.localStorage.getItem(uid) === uid
      window.localStorage.removeItem(uid)
    }
  } catch (error) {
    enabled = false
  }
  return !!enabled
}

/**
 * Some react switches & buttons may have unexpected behavior when tested at high speeds
 * This adds a custom pause after a click to ensure the switch behaves properly
 * @param {Object} page - Playwright object representing the browser page.
 * @param {String} locator - A string for identifying the react switch label
 */
const clickAndWait = async (page, locator) => {
  await page.locator(locator).click()
  await page.waitForTimeout(200)
}

module.exports = {
  assertCategories,
  assertDefaultLayers,
  assertLayerOrdering,
  clickDownload,
  closeImageDownloadPanel,
  createAreaMeasurement,
  createDistanceMeasurement,
  dateSelectorMonthDay,
  localStorageEnabled,
  openImageDownloadPanel,
  clickAndWait,
  switchProjections,
  timelineDrag,
  zoomIn,
  zoomOut
}

// @ts-check
const { test, expect } = require('playwright-test-coverage')
const createSelectors = require('../../test-utils/global-variables/selectors')
const { openImageDownloadPanel } = require('../../test-utils/hooks/wvHooks')
const { joinUrl } = require('../../test-utils/hooks/basicHooks')

let page
let selectors

const startParams = [
  'p=geographic',
  'v=-180,-90,180,90',
  'l=MODIS_Terra_CorrectedReflectance_TrueColor',
  't=2018-06-01',
  'imageDownload='
]

const defaultCoords = '180.0000'
const selectedCoords = '28.1250'

test.describe.configure({ mode: 'serial' })

test.beforeAll(async ({ browser }) => {
  page = await browser.newPage()
  selectors = createSelectors(page)
})

test.afterAll(async () => {
  await page.close()
})

test('Verify that global select is present and not selected', async () => {
  const { globalSelectInput, modalCloseButton } = selectors
  const url = await joinUrl(startParams, null)
  await page.goto(url)
  await modalCloseButton.click()
  await openImageDownloadPanel(page)
  await expect(globalSelectInput).toBeVisible()
  await expect(globalSelectInput).not.toBeChecked()
})

test('Verify that checking checkbox updates bounding-box labels', async () => {
  const { globalSelectInput, bboxTopCoords, bboxBottomCoords } = selectors
  await expect(bboxTopCoords).not.toContainText(defaultCoords)
  await globalSelectInput.click()
  await expect(bboxTopCoords).toContainText(defaultCoords)
  await expect(bboxBottomCoords).toContainText(defaultCoords)
})

test('Verify that unchecking checkbox updates bounding-box to previous', async () => {
  const { globalSelectInput, bboxTopCoords, bboxBottomCoords } = selectors
  await globalSelectInput.click()
  await expect(bboxTopCoords).not.toContainText(defaultCoords)
  await expect(bboxTopCoords).toContainText(selectedCoords)
  await expect(bboxBottomCoords).toContainText(selectedCoords)
})

/**
 * Make selections for dropdown elements.
 * @param {Object} page - Playwright object representing the browser page.
 * @param {string} element - Class or id to identify the element
 * @param {integer} index - The index position of the selection you want to make
 */
const selectOption = async (page, element, index) => {
  const selectElement = await page.locator(element)
  await selectElement.selectOption({ index })
}

/**
 * Returns an element's specific attribute value to be tested agaisnt.
 * @param {Object} page - Playwright object representing the browser page.
 * @param {string} el - Class or id to identify the element
 * @param {string} attribute - The attribute ex: 'url'
 */
const getAttribute = async (page, el, attribute) => {
  const element = await page.locator(el)
  const elementAttribute = await element.getAttribute(attribute)
  return elementAttribute
}

/**
 * Returns a URL from an array of string parameters.
 * @param {Array} startParams - Array of strings each representing URL parameters.
 * @param {string|null} lastParam - String representing an interchangable url parameter.
 */
const joinUrl = async (startParams, lastParam) => {
  if (lastParam !== null) return `http://localhost:3000/?${startParams.join('&')}${lastParam}`
  return `http://localhost:3000/?${startParams.join('&')}`
}

/**
 * Clears an input and enters a new value.
 * @param {Object} page - Playwright object representing the browser page.
 * @param {string} inputElement - Class or id to identify the input element
 * @param {string} newValue - The value you want to enter as text to the input
 */
const clearAndChangeInput = async (page, inputElement, newValue) => {
  const input = page.locator(inputElement)
  await input.clear()
  await input.fill(newValue)
}

module.exports = {
  clearAndChangeInput,
  getAttribute,
  joinUrl,
  selectOption
}

// @ts-check
const { test, expect } = require('playwright-test-coverage')
const { skipTour } = require('../../test-utils/global-variables/querystrings')
const createSelectors = require('../../test-utils/global-variables/selectors')
const {
  openImageDownloadPanel,
  closeImageDownloadPanel,
  switchProjections
} = require('../../test-utils/hooks/wvHooks')

let page
let selectors

