// @ts-check
const { defineConfig, devices } = require('@playwright/test')

module.exports = defineConfig({
  testDir: './e2e/features',
  outputDir: './test-results',
  /* Maximum time one test can run for. */
  timeout: 30 * 1000,
  expect: {
    /* Maximum time expect() should wait for the condition to be met. */
    timeout: 5000
  },
  /* Run tests in files in parallel */
  fullyParallel: true,
  /* Fail the build on CI if you accidentally left test.only in the source code. */
  forbidOnly: !!process.env.CI,
  /* Retry on CI only */
  retries: process.env.CI ? 2 : 0,
  /* Opt out of parallel tests on CI. */
  workers: process.env.CI ? 1 : 3,
  /* Reporter to use. See https://playwright.dev/docs/test-reporters */
  reporter: 'html',
  use: {
    launchOptions: {
      slowMo: 200
    },
    /* Maximum time each action such as `click()` can take. Defaults to 0 (no limit). */
    actionTimeout: 0,
    /* Collect trace when retrying the failed test. See https://playwright.dev/docs/trace-viewer */
    trace: 'on-first-retry'
  },

  /* Configure projects for major browsers */
  projects: [
    {
      name: 'chromium',
      use: { ...devices['Desktop Chrome'] }
    },

    {
      name: 'firefox',
      use: { ...devices['Desktop Firefox'] }
    }
  ]

  /* Folder for test artifacts such as screenshots, videos, traces, etc. */
  // outputDir: 'test-results/',
})

// @ts-check
const { test, expect } = require('playwright-test-coverage')
const createSelectors = require('../../test-utils/global-variables/selectors')
const { assertLayerOrdering } = require('../../test-utils/hooks/wvHooks')

let page
let selectors

const vectorsQueryString = 'http://localhost:3000/?v=-70.43215000968726,28.678203599725197,-59.81569241792232,31.62330063930118&l=GRanD_Dams,Reference_Labels_15m(hidden),Reference_Features_15m(hidden),Coastlines_15m,VIIRS_SNPP_CorrectedReflectance_TrueColor(hidden),MODIS_Aqua_CorrectedReflectance_TrueColor(hidden),VIIRS_NOAA20_CorrectedReflectance_TrueColor(hidden),MODIS_Terra_CorrectedReflectance_TrueColor'
const someGroupsQueryString = 'http://localhost:3000/?l=MODIS_Combined_Value_Added_AOD,MODIS_Combined_MAIAC_L2G_AerosolOpticalDepth,Reference_Labels_15m(hidden),Reference_Features_15m,MODIS_Terra_CorrectedReflectance_TrueColor&lg=true'
const twoGroupsQueryString = 'http://localhost:3000/?v=-107.15747724134027,-81.6706340523014,47.81381180183274,89.12472754295932&l=VIIRS_SNPP_Thermal_Anomalies_375m_All,VIIRS_NOAA20_Thermal_Anomalies_375m_All,MODIS_Combined_Value_Added_AOD,MODIS_Combined_MAIAC_L2G_AerosolOpticalDepth,Reference_Features_15m,MODIS_Terra_CorrectedReflectance_TrueColor&lg=true'
const mixedLayersGroupsDisabledQueryString = 'http://localhost:3000/?v=-107.15747724134027,-81.6706340523014,47.81381180183274,89.12472754295932&l=Reference_Features_15m,VIIRS_SNPP_Thermal_Anomalies_375m_All,MODIS_Combined_Value_Added_AOD,VIIRS_NOAA20_Thermal_Anomalies_375m_All,MODIS_Combined_MAIAC_L2G_AerosolOpticalDepth,MODIS_Terra_CorrectedReflectance_TrueColor&lg=false'

const mixedLayerIdOrder = [
  'active-Reference_Features_15m',
  'active-VIIRS_SNPP_Thermal_Anomalies_375m_All',
  'active-MODIS_Combined_Value_Added_AOD',
  'active-VIIRS_NOAA20_Thermal_Anomalies_375m_All',
  'active-MODIS_Combined_MAIAC_L2G_AerosolOpticalDepth'
]
const groupedLayerIdOrder = [
  'active-Reference_Features_15m',
  'active-VIIRS_SNPP_Thermal_Anomalies_375m_All',
  'active-VIIRS_NOAA20_Thermal_Anomalies_375m_All',
  'active-MODIS_Combined_Value_Added_AOD',
  'active-MODIS_Combined_MAIAC_L2G_AerosolOpticalDepth',
  'active-MODIS_Terra_CorrectedReflectance_TrueColor'
]
const ungroupedReorderdLayerIdOrder = [
  'active-MODIS_Combined_Value_Added_AOD',
  'active-MODIS_Combined_MAIAC_L2G_AerosolOpticalDepth',
  'active-VIIRS_SNPP_Thermal_Anomalies_375m_All',
  'active-VIIRS_NOAA20_Thermal_Anomalies_375m_All',
  'active-Reference_Features_15m'
]

test.describe.configure({ mode: 'serial' })

test.beforeAll(async ({ browser }) => {
  page = await browser.newPage()
  selectors = createSelectors(page)
})

test.afterAll(async () => {
  await page.close()
})

test('Toggle layer Info', async () => {
  const {
    firesLayer,
    infoButton,
    infoDialog,
    modalCloseButton
  } = selectors
  await page.goto(twoGroupsQueryString)
  await modalCloseButton.click()
  await firesLayer.hover()
  await infoButton.click()
  await infoButton.click()
  await expect(infoDialog).not.toBeVisible()
})

test('Toggle Layer Options', async () => {
  const { optionsButton, optionsDialog } = selectors
  await optionsButton.click()
  await optionsButton.click()
  await expect(optionsDialog).not.toBeVisible()
})

test('Layer groups are enabled by default', async () => {
  const { groupCheckbox } = selectors
  await expect(groupCheckbox).toBeVisible()
  await expect(groupCheckbox).toBeChecked()
})

test('Adding a layer causes it to appear in the appropriate group', async () => {
  const {
    addLayers,
    firesGroup,
    firesLayer,
    layersModalCloseButton,
    layersSearchField,
    modalCloseButton,
    viirsFiresCheckbox
  } = selectors
  await page.goto(someGroupsQueryString)
  await modalCloseButton.click()
  await addLayers.click()
  await layersSearchField.fill('fires')
  await viirsFiresCheckbox.click()
  await layersModalCloseButton.click()
  await expect(firesGroup).toBeVisible()
  await expect(firesLayer).toBeVisible()
})

test('Disabling groups puts all overlays into a single group', async () => {
  const {
    groupCheckbox,
    firesLayer,
    firesGroup
  } = selectors
  await groupCheckbox.click()
  await expect(firesLayer).toBeVisible()
  await expect(firesGroup).not.toBeVisible()
  const overlayGroupItems = await page.locator('#active-overlays ul > li')
  await expect(overlayGroupItems).toHaveCount(5)
})

test('Re-enabling groups restores grouping', async () => {
  const { groupCheckbox, aodGroup } = selectors
  await groupCheckbox.click()
  await expect(aodGroup).toBeVisible()
  const fireGroupItems = await page.locator('#active-Fires_and_Thermal_Anomalies ul > li')
  const aodGroupItems = await page.locator('#active-Aerosol_Optical_Depth ul > li')
  await expect(fireGroupItems).toHaveCount(1)
  await expect(aodGroupItems).toHaveCount(2)
})

test('Removing the last layer in a group removes the group', async () => {
  const {
    firesLayer,
    firesRemove,
    firesGroup
  } = selectors
  await firesLayer.hover()
  await firesRemove.click()
  await expect(firesGroup).not.toBeVisible()
})

test('Removing a group removes all layers and the group header', async () => {
  const { aodGroupHeader, aodGroup, modalCloseButton } = selectors
  await page.goto(twoGroupsQueryString)
  await modalCloseButton.click()
  await aodGroupHeader.hover()
  await page.locator('#active-Aerosol_Optical_Depth .layer-group-more-options > button').click()
  await page.locator('#active-Aerosol_Optical_Depth .layer-group-more-options #remove-group').click()
  await expect(aodGroup).not.toBeVisible()
})

test('Load with groups disabled from permalink', async () => {
  const {
    aodGroup,
    firesGroup,
    groupCheckbox,
    modalCloseButton,
    overlaysGroup
  } = selectors
  await page.goto(mixedLayersGroupsDisabledQueryString)
  await modalCloseButton.click()
  await expect(groupCheckbox).toBeVisible()
  await expect(groupCheckbox).not.toBeChecked()
  await expect(firesGroup).not.toBeVisible()
  await expect(aodGroup).not.toBeVisible()
  await expect(overlaysGroup).toBeVisible()
  const overlayGroupItems = await page.locator('#active-overlays ul > li')
  await expect(overlayGroupItems).toHaveCount(5)
})

test('Load multiple groups from permalink', async () => {
  const {
    aodGroup,
    firesGroup,
    groupCheckbox,
    modalCloseButton
  } = selectors
  await page.goto(twoGroupsQueryString)
  await modalCloseButton.click()
  await expect(groupCheckbox).toBeVisible()
  await expect(groupCheckbox).toBeChecked()
  await expect(firesGroup).toBeVisible()
  await expect(aodGroup).toBeVisible()
  const fireGroupItems = await page.locator('#active-Fires_and_Thermal_Anomalies ul > li')
  const aodGroupItems = await page.locator('#active-Aerosol_Optical_Depth ul > li')
  await expect(fireGroupItems).toHaveCount(2)
  await expect(aodGroupItems).toHaveCount(2)
})

test('Hide all...', async () => {
  const { sidebarContainer, aodGroupHeader } = selectors
  await sidebarContainer.hover()
  await aodGroupHeader.hover()
  await page.locator('#active-Aerosol_Optical_Depth .layer-group-more-options > button').click()
  await page.locator('#active-Aerosol_Optical_Depth .layer-group-more-options #hide-all').click()
  const aodGroupHiddenLayers = await page.locator('#active-Aerosol_Optical_Depth li.layer-hidden')
  const aodGroupVisibleLayers = await page.locator('#active-Aerosol_Optical_Depth li.layer-visible')
  await expect(aodGroupHiddenLayers).toHaveCount(2)
  await expect(aodGroupVisibleLayers).toHaveCount(0)
})

test('Show all...', async () => {
  const { sidebarContainer, aodGroupHeader } = selectors
  await sidebarContainer.hover()
  await aodGroupHeader.hover()
  await page.locator('#active-Aerosol_Optical_Depth .layer-group-more-options > button').click()
  await page.locator('#active-Aerosol_Optical_Depth .layer-group-more-options #show-all').click()
  const aodGroupHiddenLayers = await page.locator('#active-Aerosol_Optical_Depth li.layer-hidden')
  const aodGroupVisibleLayers = await page.locator('#active-Aerosol_Optical_Depth li.layer-visible')
  await expect(aodGroupHiddenLayers).toHaveCount(0)
  await expect(aodGroupVisibleLayers).toHaveCount(2)
})

test('Ungrouped: Removing baselayers/overlays removes the layers but not the header', async () => {
  const {
    groupCheckbox,
    firesLayer,
    overlaysGroupHeader,
    overlaysGroup
  } = selectors
  await groupCheckbox.click()
  await firesLayer.hover()
  await overlaysGroupHeader.hover()
  await page.locator('#active-overlays .layer-group-more-options > button').click()
  await page.locator('#active-overlays .layer-group-more-options #remove-group').click()
  await expect(overlaysGroup).toBeVisible()
  const overlayGroupItems = page.locator('#active-overlays ul > li')
  await expect(overlayGroupItems).toHaveCount(0)
})

test('Re-ordering groups, then disabling groups keeps individual layer order', async () => {
  const {
    aodGroupHeader,
    firesGroupHeader,
    groupCheckbox,
    modalCloseButton
  } = selectors
  await page.goto(twoGroupsQueryString)
  await modalCloseButton.click()
  const aodBoundingBox = await aodGroupHeader.boundingBox()
  const firesBoundingBox = await firesGroupHeader.boundingBox()
  // this 'steps' option is important for making the drag action work with the 'react-draggable' library
  await page.mouse.move(
    aodBoundingBox.x,
    aodBoundingBox.y,
    { steps: 10 }
  )
  await page.mouse.down()
  const x = firesBoundingBox.x + firesBoundingBox.width / 2
  const y = firesBoundingBox.y + firesBoundingBox.height / 2
  await page.mouse.move(x, y, { steps: 10 })
  await page.mouse.up()
  await page.waitForTimeout(300)
  await groupCheckbox.click()
  const layersContainer = '#active-overlays li'
  await assertLayerOrdering(page, layersContainer, ungroupedReorderdLayerIdOrder)
})

test('Enabling groups re-orders layers into their groups', async () => {
  const {
    aodGroup,
    firesGroup,
    groupCheckbox,
    modalCloseButton
  } = selectors
  await page.goto(mixedLayersGroupsDisabledQueryString)
  await modalCloseButton.click()
  const layersContainer = '#active-overlays li'
  await assertLayerOrdering(page, layersContainer, mixedLayerIdOrder)
  await groupCheckbox.click()
  await expect(aodGroup).toBeVisible()
  await expect(firesGroup).toBeVisible()
  const groupedLayersContainer = '.layer-container ul .productsitem'
  await assertLayerOrdering(page, groupedLayersContainer, groupedLayerIdOrder)
})

test('Immediately disabling groups restores mixed ordering', async () => {
  const {
    aodGroup,
    firesGroup,
    groupCheckbox
  } = selectors
  await groupCheckbox.click()
  await expect(aodGroup).not.toBeVisible()
  await expect(firesGroup).not.toBeVisible()
  const layersContainer = '#active-overlays li'
  await assertLayerOrdering(page, layersContainer, mixedLayerIdOrder)
})

test('Making a change to grouped layers causes group ordering to be retained when ungrouped', async () => {
  const { groupCheckbox, aodGroupHeader, modalCloseButton } = selectors
  await page.goto(mixedLayersGroupsDisabledQueryString)
  await modalCloseButton.click()
  await groupCheckbox.click()
  await aodGroupHeader.hover()
  await page.locator('#active-Aerosol_Optical_Depth .layer-group-more-options > button').click()
  await page.locator('#active-Aerosol_Optical_Depth .layer-group-more-options #hide-all').click()
  await groupCheckbox.click()
  const groupedLayersContainer = '.layer-container ul .productsitem'
  await assertLayerOrdering(page, groupedLayersContainer, groupedLayerIdOrder)
})

test('Vector layer has pointer icon & clicking vector layer pointer shows modal', async () => {
  const { modalCloseButton } = selectors
  await page.goto(vectorsQueryString)
  await modalCloseButton.click()
  const handPointer = await page.locator('#active-GRanD_Dams .fa-hand-pointer')
  await expect(handPointer).toBeVisible()
  await handPointer.click()
  const modalContent = await page.locator('.modal-content')
  await expect(modalContent).toContainText('Vector features may not be clickable at all zoom levels.')
})

const { expect } = require('@playwright/test')

/**
 * Switches the projection
 * @param {Object} page - Playwright object representing the browser page.
 * @param {string} proj - The specified projection ex: 'geographic', 'arctic' etc..
 */
const switchProjections = async (page, proj) => {
  await page.locator('#wv-proj-button').click()
  await page.locator(`#change-${proj}-button`).click()
}

const openImageDownloadPanel = async (page) => {
  await page.locator('#wv-image-button').click()
}

const clickDownload = async (page) => {
  await page.locator('.wv-image-button').click()
}

const closeImageDownloadPanel = async (page) => {
  await page.locator('.modal-close-btn').click()
}

const zoomIn = async (page) => {
  await page.locator('button.wv-map-zoom-in').click()
}

const zoomOut = async (page) => {
  await page.locator('button.wv-map-zoom-out').click()
}

/**
 * Drag one of the draggers on the timeline.
 * To find the x parameter you need to inspect the exact date on the timeline you want to drag to
 * and find the x attribute value in the rect element.
 * @param {Object} page - Playwright object representing the browser page.
 * @param {string} draggerEl - 'a' or 'b'. Represents draggerA or draggerB
 * @param {string} x - Represents exact point on timeline to drag to.
 */
const timelineDrag = async (page, draggerEl, x) => {
  const draggerA = await page.locator('.timeline-dragger.draggerA')
  const draggerB = await page.locator('.timeline-dragger.draggerB')
  const dragger = draggerEl === 'a' ? draggerA : draggerB
  const dragDate = `rect.axis-grid-rect[width="12"][height="65"][x="${x}"][fill="transparent"]`
  const dragLocation = await page.locator(dragDate)
  await dragger.dragTo(dragLocation)
}

/**
 * Returns the month date of date selector ex: 'AUG17'
 * @param {Object} page - Playwright object representing the browser page.
 */
const dateSelectorMonthDay = async (page) => {
  const dateSelectorDayInput = await page.locator('#date-selector-main .input-wrapper-day input')
  const dateSelectorMonthInput = await page.locator('#date-selector-main .input-wrapper-month input')
  const day = dateSelectorDayInput.innerText()
  const month = dateSelectorMonthInput.innerText()
  return month + day
}

const assertDefaultLayers = async (page) => {
  const layerItem = page.locator('.item.productsitem')
  const refLabels = page.locator('#active-Reference_Labels_15m')
  const refFeatures = page.locator('#active-Reference_Features_15m')
  const coastlines = page.locator('#active-Coastlines_15m')
  const trueColorSNPP = page.locator('#active-VIIRS_SNPP_CorrectedReflectance_TrueColor')
  const trueColorAqua = page.locator('#active-MODIS_Aqua_CorrectedReflectance_TrueColor')
  const trueColorMODIS = page.locator('#active-MODIS_Terra_CorrectedReflectance_TrueColor')
  const trueColorNOAA = page.locator('#active-VIIRS_NOAA20_CorrectedReflectance_TrueColor')
  await expect(layerItem).toHaveCount(7)
  await expect(refLabels).toBeVisible()
  await expect(refFeatures).toBeVisible()
  await expect(coastlines).toBeVisible()
  await expect(trueColorSNPP).toBeVisible()
  await expect(trueColorAqua).toBeVisible()
  await expect(trueColorMODIS).toBeVisible()
  await expect(trueColorNOAA).toBeVisible()
}

const assertCategories = async (page) => {
  const categoriesContainer = page.locator('.category-masonry-case')
  const legacy = page.locator('#legacy-all')
  const airQuality = page.locator('#air-quality')
  const ashPlumes = page.locator('#ash-plumes')
  const drought = page.locator('#drought')
  const fires = page.locator('#fires')
  const floods = page.locator('#floods')
  const shipping = page.locator('#shipping')
  const dust = page.locator('#dust-storms')
  const storms = page.locator('#severe-storms')
  const smoke = page.locator('#smoke-plumes')
  const vegetation = page.locator('#vegetation')
  const other = page.locator('#legacy-other')
  await expect(categoriesContainer).toBeVisible()
  await expect(legacy).toBeVisible()
  await expect(airQuality).toBeVisible()
  await expect(ashPlumes).toBeVisible()
  await expect(drought).toBeVisible()
  await expect(fires).toBeVisible()
  await expect(floods).toBeVisible()
  await expect(shipping).toBeVisible()
  await expect(dust).toBeVisible()
  await expect(storms).toBeVisible()
  await expect(smoke).toBeVisible()
  await expect(vegetation).toBeVisible()
  await expect(other).toBeVisible()
}

/**
 * Check the layer order in the sidebar and compare it agaisnt an array of ordered layers
 * @param {Object} page - Playwright object representing the browser page.
 * @param {string} layerContainer - A string for identifying each layer <li> element in the sidebar
 * @param {Array} orderedLayers - An array of strings representing the expected layer ordering
 */
const assertLayerOrdering = async (page, layerContainer, orderedLayers) => {
  const layers = await page.$$(layerContainer)
  const layerIDs = await Promise.all(layers.map(async (layer) => {
    const layerID = await layer.getAttribute('id')
    return layerID
  }))
  expect(layerIDs).toEqual(orderedLayers)
}

/**
 * Create a distance measurement
 * @param {Object} page - Playwright object representing the browser page.
 * @param {Array} start - An array of two integers representing the starting x & y position
 * @param {Array} finish - An array of two integers representing the starting x & y position
 */
const createDistanceMeasurement = async (page, start, finish) => {
  await page.locator('#wv-measure-button').click()
  await page.locator('#measure-distance-button').click()
  await page.mouse.click(start[0], start[1])
  await page.mouse.dblclick(finish[0], finish[1])
}

const createAreaMeasurement = async (page, pointOne, pointTwo, pointThree) => {
  await page.locator('#wv-measure-button').click()
  await page.locator('#measure-area-button').click()
  await page.mouse.click(pointOne[0], pointOne[1])
  await page.mouse.click(pointTwo[0], pointTwo[1])
  await page.mouse.dblclick(pointThree[0], pointThree[1])
}

const localStorageEnabled = () => {
  let enabled
  try {
    if (window.localStorage) {
      const uid = new Date().toString()
      window.localStorage.setItem(uid, uid)
      enabled = window.localStorage.getItem(uid) === uid
      window.localStorage.removeItem(uid)
    }
  } catch (error) {
    enabled = false
  }
  return !!enabled
}

/**
 * Some react switches & buttons may have unexpected behavior when tested at high speeds
 * This adds a custom pause after a click to ensure the switch behaves properly
 * @param {Object} page - Playwright object representing the browser page.
 * @param {String} locator - A string for identifying the react switch label
 */
const clickAndWait = async (page, locator) => {
  await page.locator(locator).click()
  await page.waitForTimeout(200)
}

module.exports = {
  assertCategories,
  assertDefaultLayers,
  assertLayerOrdering,
  clickDownload,
  closeImageDownloadPanel,
  createAreaMeasurement,
  createDistanceMeasurement,
  dateSelectorMonthDay,
  localStorageEnabled,
  openImageDownloadPanel,
  clickAndWait,
  switchProjections,
  timelineDrag,
  zoomIn,
  zoomOut
}

// @ts-check
const { test, expect } = require('playwright-test-coverage')
const createSelectors = require('../../test-utils/global-variables/selectors')

let page
let selectors

const mockAlertQuery = 'http://localhost:3000/?mockAlerts='
const layerNoticesQuery = 'http://localhost:3000/?l=Coastlines_15m,MODIS_Aqua_CorrectedReflectance_TrueColor,Particulate_Matter_Below_2.5micrometers_2001-2010'

let infoButtonIcon
let infoMenu
let notificationsListItem
let tooltipSelector

test.describe.configure({ mode: 'serial' })

test.beforeAll(async ({ browser }) => {
  page = await browser.newPage()
  selectors = createSelectors(page)
  infoButtonIcon = page.locator('#wv-info-button svg.svg-inline--fa')
  infoMenu = page.locator('#toolbar_info')
  notificationsListItem = page.locator('#notifications_info_item')
  tooltipSelector = page.locator('.tooltip-inner div')
})

test.afterAll(async () => {
  await page.close()
})

test('No visible notifications with mockAlert parameter set to no_types', async () => {
  const url = `${mockAlertQuery}no_types`
  const giftListItem = await page.locator('#toolbar_info li.gift')
  const boltListItem = await page.locator('#toolbar_info li.bolt')
  await page.goto(url)
  await infoButtonIcon.click()
  await expect(infoMenu).not.toContainText('Notifications')
  await expect(giftListItem).not.toBeVisible()
  await expect(boltListItem).not.toBeVisible()
})

test('Verify that layer notices don\'t show up in the notification list or contribute to the count', async () => {
  const { modalCloseButton } = selectors
  const url = `${layerNoticesQuery}&mockAlerts=all_types`
  await page.goto(url)
  await modalCloseButton.click()
  await infoButtonIcon.click()
  const badge = await page.getByRole('button', { name: 'Notifications 2' })
  await expect(badge).toBeVisible()
  await expect(badge).toContainText('2')
})

test('Alert and message content is highlighted and found in modal', async () => {
  const alertContentHighlighted = await page.locator('#notification_list_modal .alert-notification-item p')
  const messageContentHighlighted = await page.locator('#notification_list_modal .message-notification-item p')
  await notificationsListItem.click()
  await expect(alertContentHighlighted).toContainText('learn how to visualize global satellite imagery')
  await expect(messageContentHighlighted).toContainText('This is a message test')
})

test('Verify that the user is only alerted if they have not already stored all items in localStorage', async () => {
  const hideButton = await page.locator('#wv-info-button.wv-status-hide')
  await page.locator('.modal-close-btn').click()
  await expect(hideButton).toBeVisible()
})

test('Verify that zots show for the layers that have notices', async () => {
  const { modalCloseButton } = selectors
  const url = `${layerNoticesQuery}&mockAlerts=all_types`
  await page.goto(url)
  await modalCloseButton.click()
  const aquaZot = await page.locator('#MODIS_Aqua_CorrectedReflectance_TrueColor-zot')
  const particulateZot = await page.locator('#Particulate_Matter_Below_2__2E__5micrometers_2001-2010-zot')
  await expect(aquaZot).toBeVisible()
  await aquaZot.hover()
  const aquaNotice = tooltipSelector.first()
  const multiNotice = tooltipSelector.last()
  await expect(aquaNotice).toContainText('The Aqua / MODIS Corrected Reflectance (True Color) layer is currently unavailable.')
  await expect(multiNotice).toContainText('Several layers are experiencing delays in processing.')
  await expect(particulateZot).toBeVisible()
  await particulateZot.hover()
  const tooltip = await page.locator('.tooltip-inner div div').first()
