// @ts-check
const { defineConfig, devices } = require('@playwright/test')

module.exports = defineConfig({
  testDir: './e2e/features',
  outputDir: './test-results',
  /* Maximum time one test can run for. */
  timeout: 30 * 1000,
  expect: {
    /* Maximum time expect() should wait for the condition to be met. */
    timeout: 5000
  },
  /* Run tests in files in parallel */
  fullyParallel: true,
  /* Fail the build on CI if you accidentally left test.only in the source code. */
  forbidOnly: !!process.env.CI,
  /* Retry on CI only */
  retries: process.env.CI ? 2 : 0,
  /* Opt out of parallel tests on CI. */
  workers: process.env.CI ? 1 : 3,
  /* Reporter to use. See https://playwright.dev/docs/test-reporters */
  reporter: 'html',
  use: {
    launchOptions: {
      slowMo: 200
    },
    /* Maximum time each action such as `click()` can take. Defaults to 0 (no limit). */
    actionTimeout: 0,
    /* Collect trace when retrying the failed test. See https://playwright.dev/docs/trace-viewer */
    trace: 'on-first-retry'
  },

  /* Configure projects for major browsers */
  projects: [
    {
      name: 'chromium',
      use: { ...devices['Desktop Chrome'] }
    },

    {
      name: 'firefox',
      use: { ...devices['Desktop Firefox'] }
    }
  ]

  /* Folder for test artifacts such as screenshots, videos, traces, etc. */
  // outputDir: 'test-results/',
})

// @ts-check
const { test, expect } = require('playwright-test-coverage')
const createSelectors = require('../../test-utils/global-variables/selectors')
const { skipTour, activeAnimationWidget, animationGeostationary } = require('../../test-utils/global-variables/querystrings')

let page
let selectors

test.describe.configure({ mode: 'serial' })

test.beforeAll(async ({ browser }) => {
  page = await browser.newPage()
  selectors = createSelectors(page)
})

test.afterAll(async () => {
  await page.close()
})

test('Clicking the animation widget button opens the widget', async () => {
  const { dragger, animationWidget, animationButton, modalCloseButton } = selectors
  await page.goto(skipTour)
  await modalCloseButton.click()
  await expect(dragger).toBeVisible()
  await expect(animationWidget).not.toBeVisible()
  await animationButton.click()
  await expect(animationWidget).toBeVisible()
})

test('Opening custom interval widget', async () => {
  const { modalCloseButton } = selectors
  await page.goto(activeAnimationWidget)
  await modalCloseButton.click()
  await page.locator('.wv-animation-widget-header #timeline-interval-btn-container #current-interval').hover()
  const yearInterval = page.locator('.wv-animation-widget-header .timeline-interval .interval-years')
  await expect(yearInterval).toBeVisible()
  await page.locator('.wv-animation-widget-header .timeline-interval #interval-custom-static').click()
  const customIntervalWidget = page.locator('#wv-animation-widget .custom-interval-widget')
  const widgetHeader = page.locator('.wv-animation-widget-header #current-interval')
  const timelineInterval = page.locator('#timeline #current-interval')
  const expectedText = ('1 day')
  await expect(customIntervalWidget).toBeVisible()
  await expect(widgetHeader).toHaveText(expectedText)
  await expect(timelineInterval).toHaveText(expectedText)
})

test('Changing animation time interval', async () => {
  const { animationButton, modalCloseButton } = selectors
  await page.goto(skipTour)
  await modalCloseButton.click()
  await animationButton.click()
  await page.locator('.wv-animation-widget-header #timeline-interval-btn-container #current-interval').hover()
  const yearInterval = page.locator('.wv-animation-widget-header .timeline-interval .interval-years')
  await expect(yearInterval).toBeVisible()
  await yearInterval.click()
  const widgetHeader = page.locator('.wv-animation-widget-header #current-interval')
  const timelineInterval = page.locator('#timeline #current-interval')
  const expectedText = ('1 year')
  await expect(widgetHeader).toHaveText(expectedText)
  await expect(timelineInterval).toHaveText(expectedText)
})

test('Disable playback when max frames exceeded', async () => {
  const { modalCloseButton, playButton, yearStartInput } = selectors
  await page.goto(animationGeostationary)
  await modalCloseButton.click()
  const animateYearDown = page.locator('.wv-date-range-selector > div > div > div:nth-child(3) > svg > .downarrow').first()
  const animateYearUp = page.locator('.wv-date-range-selector > div > div > div > svg > .uparrow').first()
  await animateYearDown.click()
  await expect(playButton).toHaveClass(/disabled/)
  // Playback re-enabled when frames within the max
  await animateYearUp.click()
  await expect(playButton).not.toHaveClass(/disabled/)
  // App should not freeze when dates roll over
  await animateYearUp.click()
  await expect(yearStartInput).toHaveValue('1948')
  await expect(playButton).toHaveClass(/disabled/)
})

// @ts-check
const { test, expect } = require('playwright-test-coverage')
const createSelectors = require('../../test-utils/global-variables/selectors')
const { swipeAndAIsActive, multipleDataLayers, referenceLayersOnly } = require('../../test-utils/global-variables/querystrings')

let page
let selectors

test.describe.configure({ mode: 'serial' })

test.beforeAll(async ({ browser }) => {
  page = await browser.newPage()
  selectors = createSelectors(page)
})

test.afterAll(async () => {
  await page.close()
})

test('Charting button disabled by default', async () => {
  const { chartingButton } = selectors
  await page.goto(referenceLayersOnly)
  const isChartingEnabled = await page.$('#chart-toggle-button')
  if (isChartingEnabled) {
    await expect(chartingButton).toHaveClass(/disabled/)
  }
})

test('Comparison button is hidden when Charting mode active', async () => {
  const { compareButton } = selectors
  await page.goto(multipleDataLayers)
  const isChartingEnabled = await page.$('#chart-toggle-button')
  if (isChartingEnabled) {
    await page.locator('#chart-toggle-button').click()
    await expect(compareButton).toHaveCount(0)
    await page.locator('#chart-toggle-button').click()
  }
})

test('Charting mode is hidden when Compare mode active', async () => {
  const { chartingButton } = selectors
  await page.goto(swipeAndAIsActive)
  const isChartingEnabled = await page.$('#chart-toggle-button')
  if (isChartingEnabled) {
    await expect(chartingButton).toHaveCount(0)
  }
})

test('`Exit Charting` button is available in Charting mode', async () => {
  const { chartingButton } = selectors
  await page.goto(multipleDataLayers)
  const isChartingEnabled = await page.$('#chart-toggle-button')
  if (isChartingEnabled) {
    await page.locator('#chart-toggle-button').click()
    await expect(chartingButton).toHaveText('Exit Charting')
  }
})

test('Entering & exiting Charting Mode works properly', async () => {
  const { chartingButton } = selectors
  await page.goto(multipleDataLayers)
  const isChartingEnabled = await page.$('#chart-toggle-button')
  if (isChartingEnabled) {
    await page.locator('#chart-toggle-button').click()
    await expect(chartingButton).toHaveText('Exit Charting')
    await page.locator('#chart-toggle-button').click()
    await expect(chartingButton).toHaveText('Start Charting')
  }
})

test('Select an Area of Interest works appropriately', async () => {
  await page.goto(multipleDataLayers)
  const isChartingEnabled = await page.$('#chart-toggle-button')
  if (isChartingEnabled) {
    await page.locator('#chart-toggle-button').click()
    const aoiIcon = page.locator('#wv-charting-mode-container > div.charting-aoi-container > h3')
    await expect(aoiIcon).toHaveText('Select Area of Interest')
    await page.locator('#wv-charting-mode-container > div.charting-aoi-container > svg').click()
    await page.mouse.click(300, 300)
    await page.mouse.click(400, 400)
    await expect(aoiIcon).toHaveText('Area of Interest Selected')
  }
})

test('Confirm defaults for time span selection', async () => {
  const { chartingDateSingleButton, chartingDateRangeButton } = selectors
  await page.goto(multipleDataLayers)
  const isChartingEnabled = await page.$('#chart-toggle-button')
  if (isChartingEnabled) {
    await page.locator('#chart-toggle-button').click()
    await expect(chartingDateSingleButton).not.toHaveClass(/btn-active/)
    await expect(chartingDateRangeButton).toHaveClass(/btn-active/)
    await page.locator('#charting-date-single-button').click()
    await expect(chartingDateSingleButton).toHaveClass(/btn-active/)
    await expect(chartingDateRangeButton).not.toHaveClass(/btn-active/)
  }
})

test('Calendar icon opens datepicker modal', async () => {
  await page.goto(multipleDataLayers)
  const isChartingEnabled = await page.$('#chart-toggle-button')
  if (isChartingEnabled) {
    await page.locator('#chart-toggle-button').click()
    await page.locator('#charting-calendar-container > svg').click()
    const chartingDateModal = page.locator('#charting_date_modal > div > div > div.modal-header > h5')
    await expect(chartingDateModal).toHaveText('Charting Mode Date Selection')
  }
})

test('Info modal opens on mode start & on icon click', async () => {
  await page.goto(multipleDataLayers)
  const isChartingEnabled = await page.$('#chart-toggle-button')
  if (isChartingEnabled) {
    await page.locator('#chart-toggle-button').click()
    const infoModal = page.locator('#charting_info_modal > div > div > div.modal-header > h5')
    await expect(infoModal).toHaveText('Charting Tool')
    await page.locator('#charting_info_modal > div > div > div.modal-header > button').click()
    await expect(infoModal).toHaveCount(0)
    await page.locator('#charting-info-container > svg').click()
    await expect(infoModal).toHaveText('Charting Tool')
  }
})

test('User can toggle active layer', async () => {
  await page.goto(multipleDataLayers)
  const isChartingEnabled = await page.$('#chart-toggle-button')
  if (isChartingEnabled) {
    await page.locator('#chart-toggle-button').click()
    const layerOneAnchor = page.locator('#activate-MODIS_Terra_Aerosol')
    const layerTwoAnchor = page.locator('#activate-MODIS_Terra_Brightness_Temp_Band31_Day')
    await expect(layerOneAnchor).toHaveClass('layer-visible visibility active-chart')
    await expect(layerTwoAnchor).toHaveClass('layer-visible visibility')
    await page.locator('#activate-MODIS_Terra_Brightness_Temp_Band31_Day > svg').click()
    await expect(layerOneAnchor).toHaveClass('layer-visible visibility')
    await expect(layerTwoAnchor).toHaveClass('layer-visible visibility active-chart')
  }
})

// @ts-check
const { test, expect } = require('playwright-test-coverage')
const createSelectors = require('../../test-utils/global-variables/selectors')
const { swipeAOD } = require('../../test-utils/global-variables/querystrings')

let page
let selectors
let aerosolLayer
let AodOptionsPanelBody
let AodInfoPanel
let correctedReflectanceBLayer
let correctedReflectanceInfoPanel

test.describe.configure({ mode: 'serial' })

test.beforeAll(async ({ browser }) => {
  page = await browser.newPage()
  selectors = createSelectors(page)
  aerosolLayer = page.locator('#active-MODIS_Terra_Aerosol')
  AodOptionsPanelBody = page.locator('#layer_options_modal-modis_terra_aerosol .modal-body')
  AodInfoPanel = page.locator('.layer_info_modal-modis_terra_aerosol')
  correctedReflectanceBLayer = page.locator('#activeB-MODIS_Terra_CorrectedReflectance_TrueColor')
  correctedReflectanceInfoPanel = page.locator('#layer_info_modal-modis_terra_correctedreflectance_truecolor')
})

test.afterAll(async () => {
  await page.close()
})

test('Layer option features work in A|B mode', async () => {
  const { modalCloseButton } = selectors
  await page.goto(swipeAOD)
  await modalCloseButton.click()
  await expect(AodOptionsPanelBody).not.toBeVisible()
  await aerosolLayer.hover()
  await page.locator('#active-MODIS_Terra_Aerosol .wv-layers-options').click()
  const modalTitle = page.locator('#layer_options_modal-modis_terra_aerosol .modal-header .modal-title')
  await expect(modalTitle).toHaveText('Dark Target Aerosol Optical Depth (Land and Ocean)')
  const paletteSelector = page.locator('#layer_options_modal-modis_terra_aerosol .modal-body .wv-palette-selector')
  await expect(paletteSelector).toBeVisible()
})

test('Layer info dialog work in A|B mode', async () => {
  await page.locator('.modal-close-btn').click()
  await aerosolLayer.hover()
  await page.locator('#layer-info-btn-MODIS_Terra_Aerosol').click()
  await expect(AodInfoPanel).toContainText('The Aerosol Optical Depth layer is useful for studying aerosol optical depth')
})

test('Expect clicking A|B button to close options dialog', async () => {
  const { compareButton } = selectors
  await page.locator('.modal-close-btn').click()
  await compareButton.click()
  await expect(AodOptionsPanelBody).not.toBeVisible()
})

test('Layer option features after exiting A|B mode', async () => {
  await aerosolLayer.hover()
  await page.locator('#active-MODIS_Terra_Aerosol .wv-layers-options').click()
  const title = page.locator('#layer_options_modal-modis_terra_aerosol .modal-header .modal-title')
  await expect(title).toContainText('Aerosol Optical Depth')
  const paletteSelector = page.locator('#layer_options_modal-modis_terra_aerosol .modal-body .wv-palette-selector')
  await expect(paletteSelector).toBeVisible()
})

test('Layer info dialog works after exiting A|B mode', async () => {
  await page.locator('.modal-close-btn').click()
  await aerosolLayer.hover()
  await page.locator('#layer-info-btn-MODIS_Terra_Aerosol').click()
  await expect(AodInfoPanel).toContainText('The Aerosol Optical Depth layer is useful for studying aerosol optical depth')
})

test('Expect reactivating A|B to close options dialog and activate B state', async () => {
  const { compareButton, bTab } = selectors
  await page.locator('.modal-close-btn').click()
  await aerosolLayer.hover()
  await page.locator('#active-MODIS_Terra_Aerosol .wv-layers-options').click()
  await page.locator('.modal-close-btn').click()
  await compareButton.click()
  await expect(AodOptionsPanelBody).not.toBeVisible()
  await bTab.click()
})

test('Layer option features work in B state', async () => {
  await expect(AodOptionsPanelBody).not.toBeVisible()
  await correctedReflectanceBLayer.hover()
  await page.locator('#activeB-MODIS_Terra_CorrectedReflectance_TrueColor .wv-layers-options').click()
  const title = page.locator('#layer_options_modal-modis_terra_correctedreflectance_truecolor .modal-header .modal-title')
  await expect(title).toContainText('Corrected Reflectance (True Color')
  const paletteSelector = page.locator('#layer_options_modal-modis_terra_correctedreflectance_truecolor .modal-body .wv-palette-selector')
  await expect(paletteSelector).not.toBeVisible()
})

test('Layer info dialog works after clicking into B mode', async () => {
  await page.locator('.modal-close-btn').click()
  await correctedReflectanceBLayer.hover()
  await page.locator('#activeB-MODIS_Terra_CorrectedReflectance_TrueColor .wv-layers-info').click()
  await expect(correctedReflectanceInfoPanel).toContainText('These images are called true-color or natural color because this combination of wavelengths is similar to what the human eye')
})
